public class Sample33 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x;
		x = 1;

		switch (x) {
		case 0:
			System.out.println(0);
			break;
		case 1:
			System.out.println(1);
			break;
		case 2:
			System.out.println(2);
			break;
		case 3:
			System.out.println(3);
			break;
		default:
			System.out.println(-1);
		}
	}

}
